mapper.update:
UPDATE tb_athlete
name = #{name}, gender = #{gender}, event = #{event}, birthday = #{birthday}, photo_name = #{photoName}, update_time = #{updateTime}
WHERE id = #{id}

mapper.select:
SELECT COUNT(id) FROM tb_athlete


		team = #{team}


		AND name = #{name}


		AND gender = #{gender}


		AND birthday = #{birthday}


		AND event LIKE #{event}

