mapper.update:
UPDATE tb_athlete SET name = #{name}, gender = #{gender},
event = #{event}, birthday = #{birthday}, photo_name = #{photoName},
update_time = #{updateTime} WHERE id = #{id}

mapper.select:
SELECT COUNT(id) FROM tb_athlete WHERE team = #{team}

	AND name = #{name}


	AND gender = #{gender}


	AND birthday = #{birthday}


	AND event LIKE #{event}

SELECT * FROM tb_athlete WHERE team = #{team}

	AND name = #{name}


	AND gender = #{gender}


	AND birthday = #{birthday}


	AND event LIKE #{event}

ORDER BY id LIMIT #{offset}, #{pageSize}
